import { useState, useEffect } from 'react';
import { GifGridItem } from './GifGridItem';

export const GifGrid = ( { category } ) => {
    
    const [images, setImages] = useState([]);
    
    useEffect( () => {
        getGifs();
    }, [] );
    
    const getGifs = async() => {
        
        const url = 'https://api.giphy.com/v1/gifs/search?api_key=txfS5bbaSYS84q6l5Rs5tMXqtZ5nxkZC&q=fullmetal%20Alchemist%20brotherhood&limit=10';
        const resp = await fetch(url);
        const { data } = await resp.json();
        const gifs = data.map(img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images?.downsized_medium.url
            }
        })
        console.log(gifs);
        setImages(gifs);
    };

    return (
        <>
            <h3>{ category }</h3>
            <div className="card-grid" >
                
                {
                images.map( img => (
                    <GifGridItem 
                        key={ img.id }
                        
                        { ...img } >
                    </GifGridItem>
                ))
                }
            </div>
        </>
    )
}
