        const url = 'https://api.giphy.com/v1/gifs/search?api_key=txfS5bbaSYS84q6l5Rs5tMXqtZ5nxkZC&q=fullmetal%20Alchemist&limit=10';
        const resp = await fetch(url);
        const { data } = await resp.json();
        const gifs = data.map(img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images?.downsized_medium.url
            }
        })
        console.log(gifs);
    };
    getGifs();
